import "./App.css";
import React, { Component } from "react";

class DisplayThings extends Component {
  constructor(props) {
    super(props);

    this.state = {
      time: "1111",
      name: "",
    };
  }

  componentDidMount() {
    this.getTime();
  }

  formStateHandler = (event) => {
    this.setState({ name: event.target.value });
  };

  formSubmitHandler = (event) => {
    alert("hellooo" + this.state.name + event);
  };

  getTime() {
    fetch("http://localhost:3001")
      .then((response) => {
        return response.text();
      })
      .then((response) => {
        this.setState({ time: response });
        console.log(response);
      });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>The time is {this.state.time}.</p>
          <p> the user is {this.state.name} </p>
          <form onSubmit={formSubmitHandler}>
            <h4> Leave information about your visit</h4>
            <p> Who was the visitor?</p>
            <input type="text" onChange={formStateHandler} />
            <input type="submit" />
          </form>
        </header>
      </div>
    );
  }
}

export default DisplayThings;
