import "./App.css";
import React, { Component } from "react";

class DisplayThings extends Component {
  constructor(props) {
    super(props);

    this.state = {
      time: "",
      value: "",
      users: [],
      status: "",
    };
  }

  /* 
Refresh values to synchronize multiple sessions.
*/
  componentDidMount() {
    this.getTime();
    this.getUsers();
    this.interval = setInterval(() => this.getTime(), 3000);
    this.interval2 = setInterval(() => this.getUsers(), 6000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
    clearInterval(this.interval2);
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.users.length !== this.state.users.length) {
      this.getUsers();
    }
  }

  /*
Functions to handle updating state
*/

  formStateHandler = (event) => {
    this.setState({ value: event.target.value });
  };

  formSubmitHandler = (event) => {
    alert("form saved");
    console.log(event);
    this.setUser();
  };

  /*
Fetching values for frontpage. Server values set here.
*/

  getBackEndServer() {
    var BE_SERVER1 = "http://localhost:3001";
    var BE_SERVER2 = "http://localhost:3002";
    return BE_SERVER1;
  }

  getTime() {
    console.log("Fetching Time from " + this.getBackEndServer());
    fetch(this.getBackEndServer() + "/time")
      .then((response) => {
        if (!response.ok) {
          throw new Error("HTTP error" + response.status);
        }
        return response.text();
      })
      .then((response) => {
        this.setState({ time: response });
      });
  }

  getUsers() {
    console.log("Fetching users from " + this.getBackEndServer());
    fetch("http://localhost:3001/users")
      .then((response) => {
        if (!response.ok) {
          throw new Error("HTTP error" + response.status);
        }
        return response.json();
      })
      .then((response) => {
        this.setState({ users: response });
      });
  }

  setUser() {
    console.log("Setting user. Using server " + this.getBackEndServer());
    fetch("http://localhost:3001/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        user: this.state.value,
        information: "Setting user",
      }),
    }).then((response) => {
      if (!response.ok) {
        throw new Error("HTTP error" + response.status);
      }
      this.setState({ status: response });
      console.log(response);
    });
  }
  /*
Render front end. List of attendees, form to add attendees.
*/
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <div style={{ paddingBottom: "20%" }}>
              <h2> Attendees</h2>
              <ol>
                {this.state.users.map((user) => (
                  <li>
                    {user.name} {user.time}
                  </li>
                ))}
              </ol>
            </div>
            <div>
              <form onSubmit={this.formSubmitHandler}>
                <h4> Enter names of attendees </h4>
                <p> Name of an attendee</p>
                <input
                  type="text"
                  value={this.state.value}
                  onChange={this.formStateHandler}
                />
                <input type="submit" />
              </form>
              <h6
                style={{
                  textAlign: "left",
                  marginLeft: "35%",
                }}
              >
                {" "}
                Time is {this.state.time}
              </h6>
            </div>
          </div>
        </header>
      </div>
    );
  }
}

export default DisplayThings;
