import "./App.css";
import React, { Component } from "react";

class DisplayThings extends Component {
  constructor(props) {
    super(props);

    this.state = {
      time: "1111",
      name: "",
    };
  }

  componentDidMount() {
    this.getTime();
  }

  formStateHandler = (event) => {
    this.setState({ name: event.target.value });
  };

  formHandler = (event) => {
    alert("hellooo" + this.state.name + event);
  };

  getTime() {
    fetch("http://localhost:3001")
      .then((response) => {
        return response.text();
      })
      .then((response) => {
        this.setState({ time: response });
        console.log(response);
      });
  }
  render() {
    return <App time={this.state.time} handler={this.formStateHandler} />;
  }
}

function App(props, handler) {
  console.log("muutos");
  return (
    <div className="App">
      <header className="App-header">
        <p>The time is {props.time}.</p>
        <p> the user is {props.name} </p>
        <form>
          <h4> Leave information about your visit</h4>
          <p> Who was the visitor?</p>
          <input type="text" onChange={handler.formStateHandler} />
        </form>
      </header>
    </div>
  );
}
export default DisplayThings;
