import "./App.css";
import React, { Component } from "react";

class DisplayThings extends Component {
  constructor(props) {
    super(props);

    this.state = {
      time: "1111",
      name: "",
      status: "",
    };
  }

  componentDidMount() {
    this.getTime();
    this.interval = setInterval(
      () => this.setState({ time: this.getTime() }),
      1000
    );
  }

  componentWillUnmount() {
    this.getTime();
    clearInterval(this.interval);
  }

  formStateHandler = (event) => {
    this.setState({ name: event.target.value });
  };

  formSubmitHandler = (event) => {
    alert("form saved");
    console.log(event);
    this.setUser();
  };

  getTime() {
    fetch("http://localhost:3001")
      .then((response) => {
        return response.text();
      })
      .then((response) => {
        this.setState({ time: response });
        console.log(response);
      });
  }

  setUser() {
    fetch("http://localhost:3001", {
      method: "POST",
      body: "user",
    }).then((response) => {
      this.setState({ status: response });
      console.log(response);
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <p style={{ textAlign: "left" }}>Time is {this.state.time}</p>
            <form onSubmit={this.formSubmitHandler}>
              <h4> Leave information about your visit</h4>
              <p> Who was the visitor?</p>
              <input type="text" onChange={this.formStateHandler} />
              <input type="submit" />
            </form>
          </div>
        </header>
      </div>
    );
  }
}

export default DisplayThings;
